css:
.top-bar__countdown {
    font-size: 15px;
    padding: var(--spacer-2) var(--spacer-3);
}

/* cabeçalho */    
.header {
position: relative;
background: linear-gradient(
270deg, rgba(3,77,90,1) 0%, rgba(8,84,97,1) 25%, rgba(12,96,111,1) 50%, rgba(21,116,133,1) 75%, rgba(37,132,149,1) 100%);
border-bottom: 0px;
font-family: "Roboto",sans-serif;
}



/* barra de informações */ 
.top-bar {
    background: #E9E9E9;
    font-size: 15px;
  font-family: "Roboto",sans-serif;
}
.top-bar__page-links a {
    font-weight: 400;
}
.top-bar__nav {
  color: #3a3a3a;
  background-color: #e9e9e9;
   font-size: 15px;
}

.top-bar__nav {
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
}

/* botões menu lateral, login e sacola */    
.header__buttons .btn {
color: white;
}
.header__buttons .btn:hover {
  color: white;
 background: #0F5663;
}
.badge-primary {
    color: #fff;
    background-color: #4f4f4f00;
    font-family: "Roboto",sans-serif;
}
.header__toggler-icon {
  color: white;
}
.header__toggler-icon:hover {
  color: white;
  background: #0F5663;
}

/* Logo */  
img, svg {
    vertical-align: baseline;
}

 /* texto do menu */    
.header__nav a {
text-transform: uppercase;
color: white;
font-size: 13px;
}
.header__nav a:hover {
background-color: #0F5764;
}

.header__nav--full a {
    margin-top: var(--spacer-2);
    padding: 0 var(--spacer-3);
}

/* barra de pesquisa */  
.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 17px;
    font-size: 14px;
    line-height: var(--line-height-lg);
    height: calc(var(--line-height-lg)*var(--font-size-lg) + var(--spacer-2)*1);
}

 /* breadcrumb */

a {
    color: #212529;
    text-decoration: none;
}

a:hover {
    text-decoration: none;
  color: #212529;
}

.breadcrumb-item+.breadcrumb-item:before {
    float: none;
    padding-right: 0.5rem;
    color: #1e1e1e;
    content: ">";
    font-family: tahoma;
}

.breadcrumb {
    display: flex;
    flex-wrap: wrap;
    padding: 0.6rem 0;
    margin-bottom: 1rem;
    list-style: none;
    background-color: #ffffff;
    border-radius: 0rem;
    font-size: 13px;
    font-family: arial, tahoma, verdana, sans-serif;
}

.breadcrumb-item.active {
    color: #666666;
    font-size: 12px;
}

/*Preços pequenos*/
.prices--big {
    font-size: 20px;
    font-weight: 500;
}

/* Variantes da página do produto */
.variations__option {
    min-height: 2.3rem;
    min-width: 2.3rem;
    margin-bottom: var(--spacer-2);
    border: none;
}

.btn-light {
    color: #1a1a1a;
    background-color: #e9e9e9;
    border-color: #f8f9fa;
    border-radius: 10px;
}

/* Botão de compra */
.product__buy button {
text-transform: none;
transition: 0.5s;
background: #F1F1F1;
background-size: 200% auto;
color: #212529;            
box-shadow: 0 0 20px #eee;
border-radius: 4px;
display: block;
width: 100%;
}

.product__buy button:hover {
background: #DEDEDE;
color: #212529;
text-decoration: none;
}


