/* Preços do container */
.prices:not(.prices--big) {
    font-size: 20px;
    font-weight: 500;
}

.container, .container-fluid, .container-lg, .container-md, .container-sm, .container-xl {
    width: 100%;
    padding-right: 70px;
    padding-left: 70px;
    margin-right: auto;
    margin-left: auto;
}

/* Aleatório */
.mt-3, .my-3 {
    margin-top: 1rem;
    font-size: 14px;
    color: #ba0c2f;
}

.product__discount {
    color: #810000;
}

.lead {
    font-weight: 400;
}
.prices--literal small {
