css:
.header {
background: #15616F;
}

#product .prices__value {
    color: #00725E !important;
}

.prices--big .prices__value {
    font-size: var(--h4);
    margin-bottom: var(--spacer-1);
}

.product__buy button {
color: #ffffff;
background-color: #58C22E;
border-color: #58C22E;
width: 100%;
border-radius: 4px 4px 4px 4px;
}
.product__buy button:hover {
background: #48A524;
border-color: #48A524;
}

.btn-primary {
color: #fff;
background-color: #58C22E;
border-color: #58C22E;
border-radius: 4px 4px 4px 4px;
}
.btn-primary: hover {
background: #48A524;
border-color: #48A524;
}

.product-card__buy button {
background-color: #58C22E;
border-color: #58C22E;
color: #fff;
}
.product-card__buy button:hover  {
background-color: #58C22E;
border-color: #58C22E;
color: #fff;
}

.footer {
background: #EAEAEA;
}
