css:
/* cabeçalho */    
.header {
position: relative;
background: #15616f;
border-bottom: 0px;
font-family: "Roboto",sans-serif;
}



/* barra de informações */ 
.top-bar {
    background: #0F5764;
}
.top-bar__page-links a {
    font-weight: 400;
}
.top-bar__nav {
  color: white;
  background-color: #0F5764;
}

/* botões menu lateral, login e sacola */    
.header__buttons .btn {
color: #ffffff;
}
.header__buttons .btn:hover {
color: #ffffff;
}
.header__toggler-icon {
  color: #ffffff;
}

 /* texto do menu */    
.header__nav a {
text-transform: capitalize;
color: white;
}
.header__nav a:hover {
background-color: #0F5764;
}

 /* breadcrumb */   
.mt-sm-3, .my-sm-3 {
    margin-top: 0rem;
}
.breadcrumb {
    display: flex;
    flex-wrap: wrap;
    padding: 0.5rem 0;
    margin-bottom: 1rem;
    list-style: none;
    background-color: #f9f9f9;
    border-radius: 0rem;
}

/* Corpo da página */
body {
    margin: 0;
    font-family: "Roboto",sans-serif;
    font-size: 14px;
    font-weight: 4;
    line-height: 1;
    color: #212529;
    text-align: left;
    background-color: #fff;
}
