css:
.top-bar__countdown {
    font-size: 14px;
    padding: var(--spacer-2) var(--spacer-3);
}

/* cabeçalho */    
.header {
position: relative;
background: #034d5a;
border-bottom: 0px;
font-family: "Roboto",sans-serif;
}



/* barra de informações */ 
.top-bar {
    background: #0F5764;
    font-size: 13px;
  font-family: "Roboto",sans-serif;
}
.top-bar__page-links a {
    font-weight: 400;
}
.top-bar__nav {
  color: white;
  background-color: #0F5764;
}

.top-bar__nav {
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
}

/* botões menu lateral, login e sacola */    
.header__buttons .btn {
color: white;
}
.header__buttons .btn:hover {
  color: white;
 background: #0F5663;
}
.badge-primary {
    color: #fff;
    background-color: #4f4f4f00;
    font-family: "Roboto",sans-serif;
}
.header__toggler-icon {
  color: white;
}
.header__toggler-icon:hover {
  color: white;
  background: #0F5663;
}

 /* texto do menu */    
.header__nav a {
text-transform: capitalize;
color: white;
}
.header__nav a:hover {
background-color: #0F5764;
}

/* barra de pesquisa */  
.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 17px;
    font-size: 14px;
    line-height: var(--line-height-lg);
    height: calc(var(--line-height-lg)*var(--font-size-lg) + var(--spacer-2)*1);
}

 /* breadcrumb */   
.mt-sm-3, .my-sm-3 {
    margin-top: 0rem;
}
.breadcrumb {
    display: flex;
    flex-wrap: wrap;
    padding: 0.5rem 0;
    margin-bottom: 1rem;
    list-style: none;
    background-color: #f9f9f9;
    border-radius: 0rem;
}

/* Corpo da página */


/* Botão de compra */
.product__buy button {
color: #ffffff;
background-color: #17A2B8;
border-color: white;
width: 100%;
border-radius: 15px 15px 15px 15px;
}
.product__buy button:hover {
background: #17A2B8;
border-color: white;
}
