css:
.top-bar__countdown {
    font-size: 15px;
}
header {
   position: absolute;
}

main .header {
    border-bottom: none;
    background-color: #15616f;
}

.tiao-header .header-a.strip-multi-colors {
    margin-top: -5px;
    float: left;
    height: 5px;
    width: 100%;
    overflow: hidden;
}

main .header__nav a {
    color: #ffffff;
}

.header__nav--full a {
    margin-top: var(--spacer-1);
    padding: 0 var(--spacer-4);
}

.header__nav a {
    text-transform: lowercase;
    font-weight: none;
}

element.style {
    background: #0f5764;
    color: #ffffff;
}

a {
    color: #000000;
    /* text-decoration: none; */
}

.h1, h1 {
    font-size: calc(1.375rem + 0.8vw);
  color: #666666;
}

#product .prices__value {
    color: #666666 !important;
}

.prices--big .prices__value {
    font-size: var(--h4);
    margin-bottom: var(--spacer-1);
}

.h5, h5 {
    font-size: 1rem;
}

.variations__option {
    min-height: 2.3rem;
    min-width: 2.3rem;
    margin-bottom: var(--spacer-2);
    border: var(--border-width) solid #ffffff;
  border-radius: 25px;
}

.variations__option--selected:not(.disabled) {
    border-color: none;
    box-shadow: 0 0 0 0.1rem rgba(var(--secondary-rgb),.6);
}

.btn-light {
    color: #212529;
    background-color: #eeeeee;
}

.product__buy button {
color: #ffffff;
background-color: #58C22E;
border-color: #58C22E;
width: 100%;
border-radius: 4px 4px 4px 4px;
}
.product__buy button:hover {
background: #48A524;
border-color: #48A524;
}

.btn-primary {
color: #fff;
background-color: #58C22E;
border-color: #58C22E;
border-radius: 4px 4px 4px 4px;
}
.btn-primary: hover {
background: #48A524;
border-color: #48A524;
}

.lead {
  font-size: 0.9rem;
}

.table td, .table th {
    padding: 0.9375rem 1.875rem;
    vertical-align: top;
    border-top: 1px solid #ffffff;
    font-size: .775rem;
}

.product-card__buy button {
background-color: #58C22E;
border-color: #58C22E;
color: #fff;
}
.product-card__buy button:hover  {
background-color: #58C22E;
border-color: #58C22E;
color: #fff;
}

.footer {
background: #EAEAEA;
}
